00; MotorControl.CurrentDPID.MaxError=3000; MotorControl.CurrentDPID.MinError=-3000; MotorControl.CurrentPID.MaxError=3000; MotorControl.CurrentPID.MinError=-3000; MotorControl.CurrentQPID.KpDiv=10; MotorControl.CurrentDPID.KpDiv=10; MotorControl.CurrentPID.KpDiv=10; MotorControl.SpeedPID.MaxOutput=10000;//+SpeedACTFilter.NOut>>12; MotorControl.SpeedPID.MinOutput=-10000; MotorControl.SpeedPID.MaxError=10000; MotorControl.SpeedPID.MinError=-10000; MotorControl.SpeedPID.KpDiv=10; MotorControl.VoltagePID.MaxOutput=0x7000; MotorControl.VoltagePID.MinOutput=-1*0x7000; MotorControl.VoltagePID.MaxError=1000; MotorControl.VoltagePID.MinError=-1000; MotorControl.VoltagePID.KpDiv=1; FilterInit(&MotorControl.CurrentDFilter); MotorControl.CurrentDFilter.NTime=1500; FilterInit(&MotorControl.ThorttleVoltage); MotorControl.ThorttleVoltage.NTime=5000; ThorttleVoltage.yk_1=0; ThorttleVoltage.coef=4700; CurrentDFilter.yk_1=0; CurrentDFilter.coef=4700*5; } void I_Park(void) { /*Park变换，电流由两相旋转坐标系到两相静止坐标系*/ /*输入变量：VoltageD,VoltageQ, AngleSin,AngleCos,输出变量VoltageX,VoltageY*/ /* VoltageX=VoltageD*AngleCos-VoltageQ*AngleSin; VoltageY=VoltageD*AngleSin+VoltageQ*AngleCos; */ int Temp1,Temp2; Temp1=MotorControl.VoltageD; Temp2=MotorControl.VoltageQ; MotorControl.VoltageX=((Temp1*MotorControl.AngleCos)-(Temp2*MotorControl.AngleSin))>>14; MotorControl.VoltageY=((Temp2*MotorControl.AngleCos)+(Temp1*MotorControl.AngleSin))>>14; } void MyPark(void) { /*Park变换，电流由两相静止坐标系到两相旋转坐标系*/ /*输入变量：CurrentX,CurrentY, AngleSin,AngleCos,输出变量CurrentD,CurrentQ*/ /* CurrentD=CurrentX*AngleCos+CurrentY*AngleSin; CurrentQ=-CurrentX*AngleSin+CurrentY*AngleCos; */ MotorControl.CurrentD=(long int)((long int)(MotorControl.CurrentX*MotorControl.AngleCos)+(long int)(MotorControl.CurrentY*MotorControl.AngleSin))>>14; MotorControl.CurrentQ=(long int)((long int)(MotorControl.CurrentY*MotorControl.AngleCos)-(long int)(MotorControl.CurrentX*MotorControl.AngleSin))>>14; } void SinCosGet2(void) { int Temp; Temp=MotorControl.Stop