ta_h // 2, delta_h - (delta_h // 2) left, right = delta_w // 2, delta_w - (delta_w // 2) # Add padding to the resized image color = [0, 0, 0] # Padding color (black) img_padded = cv2.copyMakeBorder(img_resized, top, bottom, left, right, cv2.BORDER_CONSTANT, value=color) return img_padded # Load and preprocess your images and HTML # Function to load images and corresponding HTML def load_and_preprocess_data(screenshots_dir, html_dir, image_height, image_width): images = [] all_html_sources = [] for filename in sorted(os.listdir(screenshots_dir)): if filename.endswith(\\".png\\"): # Load and preprocess each image img_path = os.path.join(screenshots_dir, filename) img = cv2.imread(img_path) img = resize_image_with_padding(img, image_width, image_height) img = img / 255.0 # Normalize pixel values to [0, 1] images.append(img) # Find corresponding HTML file with the same base name base_name = os.path.splitext(filename)[0] html_filename = base_name + \\".html\\" html_path = os.path.join(html_dir, html_filename) if os.path.isfile(html_path): with open(html_path, \\"r\\", encoding=\\"utf-8\\") as file: html_content = file.read() all_html_sources.append(html_content) return np.array(images), all_html_sources # Paths to your data directories screenshots_dir = \\"screenshots/\\" html_dir = \\"html/\\" images, all_html_sources = load_and_preprocess_data(screenshots_dir, html_dir, IMAGE_HEIGHT, IMAGE_WIDTH) # Prepare the tokenizer on the HTML tokenizer = Tokenizer(num_words=NUM_HTML_TOKENS) tokenizer.fit_on_texts(all_html_sources) sequences = tokenizer.texts_to_sequences(all_html_sources) html_sequences = pad_sequences(sequences, maxlen=MAX_SEQUENCE_LENGTH, padding=\\"post\\") # Convert html_sequences to one-hot-encoded for use with categorical crossentropy html_sequences_one_hot = to_categorical(html_sequences, num_classes=NUM_HTML_TOKENS) # Step 1: Create the CNN model to extract features from the image input_image = Input(shape=(IMAGE_HEIGHT, IMAGE_WIDTH, NUM_CHANNELS)) cnn = 